class AlgoTypes(Enum):
    MovingWindow = 1
    FixedWindow = 2

class RateLimitAlgoProvider:
    map = {
        AlgoTypes.MovingWindow: 'MovingWindow'
    }

    @staticmethod
    def get_class(algo_type):
        return RateLimitAlgoProvider.map.get(algo_type)

class IRateLimiting:
    def is_request_allowed(self):
        raise NotImplementedError

class MovingWindow(IRateLimiting):
    def __init__(self, capacity, time_window):
        self.capacity = capacity
        self.time_window = time_window
        self.queue = deque()
        self.lock = Lock()

    def clean_up(self, current_time):
        while self.queue and current_time - self.queue[0] > self.time_window:
            self.queue.popleft()

    def is_request_allowed(self):
        with self.lock:
            current_time = int(time.time())
            self.clean_up(current_time)
            if len(self.queue) < self.capacity:
                self.queue.append(current_time)
                return True
            return False

class RateLimiter:
    _instance = None

    def __init__(self, configuration):
        self.config = configuration
        self.rate_limit_instance_map = {}

    @classmethod
    def get_instance(cls, configuration):
        if cls._instance is None:
            cls._instance = RateLimiter(configuration)
        return cls._instance

    def get_rate_limit_instance(self):
        algo_type = self.config.get('algoType')
        algo_class_name = RateLimitAlgoProvider.get_class(algo_type)
        if algo_class_name == 'MovingWindow':
            algo_class = MovingWindow
        else:
            raise ValueError('Unsupported algorithm type')

        capacity = self.config.get('capacity')
        time_period = self.config.get('timePeriod')
        return algo_class(capacity, time_period)

    def is_request_allowed(self, key):
        if key in self.rate_limit_instance_map:
            rate_limiting = self.rate_limit_instance_map[key]
        else:
            rate_limiting = self.get_rate_limit_instance()
            self.rate_limit_instance_map[key] = rate_limiting
        return rate_limiting.is_request_allowed()

def main():
